import * as React from "react";
import { useState } from "react";

// const initialItems = [
//   { id: 1, description: "Passports", quantity: 2, packed: true },
//   { id: 2, description: "Socks", quantity: 12, packed: true },
//   { id: 3, description: "Charger", quantity: 1, packed: false },
// ];

export default function App() {
  const [items, setItems] = useState([]);

  function handleAddItems(item) {
    setItems((items) => [...items, item]);
  }

  function handleDeleteItem(id) {
    setItems((items) => items.filter((item) => item.id !== id));
  }

  function handleToggleItem(id) {
    setItems((items) =>
      items.map((item) =>
        item.id === id ? { ...item, packed: !item.packed } : item
      )
    );
  }

  function handleClearItems() {
    setItems([]);
  }

  return (
    <div className="app">
      <Logo />
      <Form handleAddItems={handleAddItems} />
      <PackingList
        items={items}
        handleDeleteItem={handleDeleteItem}
        handleToggleItem={handleToggleItem}
        handleClearItems={handleClearItems}
      />
      <Stats items={items} />
    </div>
  );
}

function Logo() {
  return <h1>üèùÔ∏è Far Away üèùÔ∏è</h1>;
}

function Form({ handleAddItems }) {
  const [description, setDescription] = useState("");
  // console.log(description);
  const [quantity, setQuantity] = useState(1);

  function handleChange(e) {
    // console.log(e);
    setDescription(e.target.value);
  }

  function handleSelect(e) {
    console.log(e);
    // const options = e.target;
    // for (let i = 1; i < options.length; i++) {
    //   if (options[i].selected) {
    //     console.log(options[i].value);
    //     setQuantity(options[i].value);
    //   }
    // }
    setQuantity(Number(e.target.value));
  }

  function handleSubmit(e) {
    e.preventDefault();
    // console.log(e);
    if (!description) return;

    const newItem = { description, quantity, packed: false, id: Date.now() };
    console.log(newItem);

    handleAddItems(newItem);

    setDescription("");
    setQuantity(1);
  }

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your üòç trip?</h3>
      <select value={quantity} onChange={handleSelect}>
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>
            {num}
          </option>
        ))}
        {/* {Array.from({ length: 20 }, (_, i) => i + 1).filter((num) => (
          {e.target[num].selected === true} &&
          <option value={num} key={num}>{num}</option>
        ))} */}
      </select>
      <input
        type="text"
        placeholder="Item..."
        value={description}
        onChange={handleChange}
      />
      <button>Add</button>
    </form>
  );
}

function PackingList({
  items,
  handleDeleteItem,
  handleToggleItem,
  handleClearItems,
}) {
  const [sortBy, setSortBy] = useState("input");

  function handleSortBy(e) {
    setSortBy(e.target.value);
  }

  function sortByDescription(items) {
    // return items.sort((a, b) => {
    //   if (a.description < b.description) {
    //     return -1;
    //   }
    //   return 1;
    // });
    return items
      .slice()
      .sort((a, b) => a.description.localeCompare(b.description));
  }

  function sortByPackedStatus(items) {
    return items.slice().sort((a, b) => Number(a.packed) - Number(b.packed));
  }

  function sortItems(items) {
    if (sortBy === "input") {
      return items;
    } else if (sortBy === "description") {
      return sortByDescription(items);
    } else return sortByPackedStatus(items);
  }

  return (
    <div className="list">
      <ul>
        {sortItems(items).map((item) => (
          <Item
            item={item}
            key={item.id}
            handleDeleteItem={handleDeleteItem}
            handleToggleItem={handleToggleItem}
          />
        ))}
      </ul>
      <div className="actions">
        <select value={sortBy} onChange={handleSortBy}>
          <option value="input">Sort by input order</option>
          <option value="description">Sort by description</option>
          <option value="packed">Sort by packed status</option>
        </select>
        <button onClick={handleClearItems}>Clear list</button>
      </div>
    </div>
  );
}

function Item({ item, handleDeleteItem, handleToggleItem }) {
  return (
    <li>
      <input
        id={item.id}
        type="checkbox"
        value={item.packed}
        onChange={() => handleToggleItem(item.id)}
      />
      <label
        htmlFor={item.id}
        style={item.packed ? { textDecoration: "line-through" } : {}}
      >
        {item.quantity} {item.description}
      </label>
      <button onClick={() => handleDeleteItem(item.id)}>‚ùå</button>
    </li>
  );
}

function Stats({ items }) {
  if (!items.length)
    return (
      <em className="stats">
        <p>Start adding some items to your packing list üöÄ</p>
      </em>
    );

  const numAllItems = items.length;

  const numPackedItems = items.filter((item) => item.packed).length;

  const percentage = Math.round((numPackedItems / numAllItems) * 100);

  return (
    <footer className="stats">
      <em>
        {percentage === 100
          ? "You got everything! Ready to go ‚úàÔ∏è"
          : `üß≥ You have ${numAllItems} items on your list, and you already packed
        ${numPackedItems} (${percentage}%)`}
      </em>
    </footer>
  );
}
